import { createStore } from "vuex";

export default createStore({
  state: {
    showView: true,
    showLogin: true,
    showReg: true,
  },
  mutations: {
    changeStyle(state, slv) {
      state.showView = slv;
    },
    changeLogin(state, rew) {
      state.showLogin = rew;
    },
    changeReg(state, rew) {
      state.showReg = rew;
    },
  },
});
